css:
@media (min-width:993px){
  .page--home .custom-html:nth-child(4) {
      background: url(/img/uploads/bg-new-blow.jpg) no-repeat top;
  }
.page--home .custom-html:nth-child(5) {
    background: url(/img/uploads/neww-blo-box.jpg) no-repeat top;
}

#influencers {
    background: url(/img/uploads/influ-new.jpg) no-repeat left top #c1eaf2;
}


.box-bg-gummie {
    padding-left: 18%;
        padding-top: 75px;
    max-width: 1056px;
}

.box-bg-gummie .title-blow {
    background: url(/img/uploads/blow-gummies.png) no-repeat 0;
    width: 240px;
    height: 108px;
}

.box-bg-gummie .oque-sao {
    display: flex;
    flex-direction: row;
    margin-right: 0;
      margin-bottom: 25px;
}

.box-bg-gummie .oque-sao h2 {
    font-size: 23px;
    margin: 15px;
    max-width: 520px;
    font-family: GoldplayAltBold;
}

.text-gummie {
    display: flex;
    flex-direction: column;
}

.box1__sobre {
    color: #303131;
    font-size: 18px;
    font-family: 'GoldplayMedium';
    padding-right: 60px;
}

.box2__sobre {
    color: #f3237f;
    font-size: 18px;
    font-family: 'GoldplayMedium';
    padding-right: 60px;
    line-height: 27px;
}

  #cta {
    background:#ed2a56;
    color:#fff;
    padding: 11px;
    max-width: 154px;
    border-radius: 8px;
    margin: 15px 0;
}

}


@media (min-width: 0px) and (max-width: 767px) {

  .box-bg-gummie .oque-sao h2 {
    font-size: 24px;
    margin: 15px;
    max-width: 520px;
    font-family: GoldplayAltBold;
    text-align: left;
}

.box-bg-gummie {
    padding-left: 0;
    padding-top: 350px;
    text-align: left;
}

.box1__sobre {
    color: #303131;
    font-size: 18px;
    font-family: 'GoldplayMedium';
}

.text-gummie {
  display:flex;
  flex-direction: column;
}

.text-sobre, .text-gummie {
    padding: 0px 15px;
}

.box2__sobre {
    color: #f3237f;
    font-size: 18px;
    font-family: 'GoldplayMedium';
    line-height: 27px;
    margin: 15px 0;
}

#cta {
    background: #ed2a56;
    color: #fff;
    padding: 11px;
    max-width: 100%;
    border-radius: 8px;
    margin: 15px 0;
    text-align: center;
}

.text-sobre {
    flex-direction: column;
}

.page--home .custom-html:nth-child(5) {
  background: url(/img/uploads/blow-gumm-new.jpg) no-repeat top;
}

.page--home .custom-html:nth-child(4) {
    background: url(/img/uploads/neww-bg-mobile-box.jpg) no-repeat top #a9f5ff;
}

#influencers {
    background: url(/img/uploads/influ-new.jpg) no-repeat top #c1eaf2;
}

}

.box_composicao h1 {
    font-family: GoldplayAltBold;
    color: #c2eaf3;
    background: url(/img/uploads/bg_composicao_title.png) no-repeat left bottom;
    padding-bottom: 30px;
}

.__pages_blow-gummies-composicao .container {
    max-width: 100%;
}

.perguntas{
    width:100%;
}

.perguntas .container {
    display:flex;
    flex-direction:column;
    align-items:center;
}


.perguntas .container .titulo{
  font-family: var(--font);
  font-size: 50.5px;
  font-weight: normal;
  font-stretch: normal;
  font-style: normal;
  line-height: 0.93;
  letter-spacing: normal;
  text-align: center;
  color: #fff;
  margin-top:84px;
  margin-bottom:79px;
}




.fundo_perguntas{
    width:100%;
    display:flex;
}

.fundo_perguntas .esq {
    display:flex;
    flex-direction:column;
    width: 50%;
}

.fundo_perguntas .dir {
    display:flex;
    flex-direction:column;
    width: 50%;
    padding-left: 10px;
}
.fundo_perguntas .risco{
    width:2px;
    height:100%;
    background:#FFF;
    margin:0 56px;
    display:block;
}
.fundo_perguntas span{
    font-family: var(--font);
    font-size: 31px;
    font-weight: normal;
    font-stretch: normal;
    font-style: normal;
    line-height: 1;
    letter-spacing: normal;
    text-align: left;
    color: #fff;
    margin-bottom:20px;
    margin-left:30px;
}

.fundo_perguntas .item{
    padding: 22px 0px 35px 0px;
    cursor: pointer;
    background: url(/img/uploads/bg_composicao_buttom.png) no-repeat left top;
    margin-bottom: 25px;
}


.fundo_perguntas .item .pergunta{
    font-size: 19px;
    font-weight: normal;
    font-stretch: normal;
    font-style: normal;
    line-height: 1;
    letter-spacing: normal;
    text-align: left;
    font-family: GoldplayAltBold;
    color: #d62b89;
}

.fundo_perguntas .item .resposta {
    display: none;
    font-size: 14px;
    font-weight: normal;
    font-stretch: normal;
    font-style: normal;
    line-height: 1.5;
    letter-spacing: normal;
    text-align: left;
    color: #d62b89;
    margin-top: 10px;
    text-align: justify;
    padding: 30px 10px;
    min-height: 270px;
}

.perguntas a{
    background:#04b900;
    border-radius: 20px;
    display:flex;
    align-items:center;
    justify-content:center;
    font-family: var(--font);
    font-size: 22px;
    font-weight: normal;
    font-stretch: normal;
    font-style: normal;
    line-height: 1;
    letter-spacing: normal;
    text-align: center;
    color: #fff;
    align-self: center;
    padding:36px 39px;
    margin:39px 0px; 
}

.perguntas a span{
    text-decoration:underline;
    margin-left:5px;
}



.box_composicao h3 {
    font-size: 16px;
    color: #fff;
    font-family: GoldplayRegular;
    line-height: 25px;
}

.box_composicao {
    padding-left: 330px;
    font-size: 14px;
    padding-top: 140px;
    text-align: justify;
    padding-right: 40px;
  max-width: 1280px;
}

.box_produtos {
    display: grid !important;
    grid-template-columns: 45% 55%;
    align-items: center;
}

.box_texts {
    margin-left: -4px;
}

.composi h3 {
    margin-left: -60px;
}

.composi {
    display: grid;
    grid-template-columns: 25% 25% 25% 25%;
    padding: 50px;
}

.composi h3 {
    font-weight: 700;
    font-size: 29px;
}

.box_texts img {
    width: 18%;
}
.composi h4 {
    font-size: 13px;
    text-align: justify;
    padding: 0px 15px 0px 0px;
}

.__pages_blow-gummies-composicao .extra-page{
    background: url(/img/uploads/bg_composicao.jpg) no-repeat center top;
}

.__pages_blow-gummies-composicao .page-title {
    display:none
}

@media(min-width:993px) and (max-width:1440px){
.composi {
    display: grid;
    grid-template-columns: 50% 50%;
    padding: 50px;
}
}

@media(max-width:767px){
.__pages_blow-gummies-composicao .extra-page {
    background: url(/img/uploads/bg_composicao_mob.jpg) no-repeat center top;
}
  .box_composicao h3 {
    font-size: 13px;
    line-height: 20px;
    color: #d62b89;
}
  .composi h3 {
    font-size: 24px;
    margin-left: 0px;
}
  .fundo_perguntas .esq, .fundo_perguntas .dir {
    width: 100%;
    padding-left: 0px;
}
  .box_composicao {
    padding-left: 0;
    font-size: 14px;
    padding-top: 500px;
    text-align: justify;
    padding-right: 0;
  }
  .fundo_perguntas {
    flex-direction: column;
}
  .box_texts img {
    width: 25%;
    margin-right: 10px;
}
  .composi {display: flex;padding: 15px;flex-direction: column;}
  .fundo_perguntas .item {
    background-size: contain;
    margin-bottom: 5px;
    padding: 15px 0px 35px 0px;
}
  .box_produtos {
    display: grid !important;
    grid-template-columns: 40% 60%;
    align-items: center;
    margin-bottom: 30px;
}
  .composi h4 {
    padding: 0;
    font-size: 11px;
    padding-right: 5px;
}
  .fundo_perguntas .item .pergunta {
        font-size: 17px;
  }
  .box_composicao h1 {
    font-size: 25px;
    text-align: center;
    background: url(/img/uploads/bg_composicao_title.png) no-repeat center bottom;
}
}
